import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";
import { Navbar, Nav, Button, Modal, Form, Container, Row, Col } from "react-bootstrap";

function DoctorProfile() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [profile, setProfile] = useState({
    firstName: "",
    lastName: "",
    Phone: "",
    Email: "",
    Address: "",
    dept: "",
  });
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/doctors/${id}`);
        setProfile(response.data);
      } catch (error) {
        console.error("Error fetching doctor profile:", error);
      }
    };

    fetchProfile();
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProfile({
      ...profile,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:5000/api/updatedoctors/${id}`, profile);
      alert("Profile updated successfully");
      setShowModal(false);
    } catch (error) {
      console.error("Error updating profile:", error);
    }
  };

  const handleShowModal = () => setShowModal(true);
  const handleCloseModal = () => setShowModal(false);

  const handleBackClick = () => {
    navigate(-1); // Navigates to the previous page
  };

  return (
    <>
      <Container className="mt-3">
        <Row>
          <Col className="d-flex justify-content-start">
            <Button variant="outline-secondary" className="mb-3" onClick={handleBackClick}>
              Back
            </Button>
          </Col>
        </Row>
      </Container>

      <Navbar bg="dark" variant="dark" expand="lg">
        <Container>
          <Navbar.Brand href="#">Doctor Profile</Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">
              <Nav.Item className="me-3">
                <span className="text-white">
                  <strong>Name:</strong> {profile.firstName} {profile.lastName}
                </span>
              </Nav.Item>
              <Nav.Item className="me-3">
                <span className="text-white">
                  <strong>Phone:</strong> {profile.Phone}
                </span>
              </Nav.Item>
              <Nav.Item className="me-3">
                <span className="text-white">
                  <strong>Email:</strong> {profile.Email}
                </span>
              </Nav.Item>
              <Nav.Item className="me-3">
                <span className="text-white">
                  <strong>Address:</strong> {profile.Address}
                </span>
              </Nav.Item>
              <Nav.Item>
                <span className="text-white">
                  <strong>Specialization:</strong> {profile.dept}
                </span>
              </Nav.Item>
            </Nav>
          </Navbar.Collapse>
          <br />
          <Button
            variant="outline-light"
            onClick={handleShowModal}
            className="ml-auto"
          >
            Update Profile
          </Button>
        </Container>
      </Navbar>

      <Container className="mt-5">
        <Row className="justify-content-center">
          <Col md={15} className="text-center">
            <h3>Welcome, Dr. {profile.firstName} {profile.lastName}</h3>
            <p>Here you can view and update your profile information.</p>
          </Col>
        </Row>
      </Container>

      <Modal show={showModal} onHide={handleCloseModal} centered>
        <Modal.Header closeButton>
          <Modal.Title>Update Profile</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleSubmit}>
            <Form.Group controlId="formFirstName" className="mb-3">
              <Form.Label>First Name</Form.Label>
              <Form.Control
                type="text"
                name="firstName"
                value={profile.firstName}
                onChange={handleChange}
                required
              />
            </Form.Group>
            <Form.Group controlId="formLastName" className="mb-3">
              <Form.Label>Last Name</Form.Label>
              <Form.Control
                type="text"
                name="lastName"
                value={profile.lastName}
                onChange={handleChange}
                required
              />
            </Form.Group>
            <Form.Group controlId="formPhone" className="mb-3">
              <Form.Label>Phone</Form.Label>
              <Form.Control
                type="number"
                name="Phone"
                value={profile.Phone}
                onChange={handleChange}
                required
              />
            </Form.Group>
            <Form.Group controlId="formEmail" className="mb-3">
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="email"
                name="Email"
                value={profile.Email}
                onChange={handleChange}
                required
              />
            </Form.Group>
            <Form.Group controlId="formAddress" className="mb-3">
              <Form.Label>Address</Form.Label>
              <Form.Control
                type="text"
                name="Address"
                value={profile.Address}
                onChange={handleChange}
                required
              />
            </Form.Group>
            <Form.Group controlId="formSpecialization" className="mb-3">
              <Form.Label>Specialization</Form.Label>
              <Form.Control
                type="text"
                name="dept"
                value={profile.dept}
                onChange={handleChange}
                required
              />
            </Form.Group>

            <div className="text-end">
              <Button variant="secondary" onClick={handleCloseModal} className="me-2">
                Cancel
              </Button>
              <Button variant="primary" type="submit">
                Save Changes
              </Button>
            </div>
          </Form>
        </Modal.Body>
      </Modal>
    </>
  );
}

export default DoctorProfile;
