import React, { useState, useEffect } from 'react';
import { Button, Table, Container, Row, Col, Card, Nav } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';

const AdminPage = () => {
  const [doctors, setDoctors] = useState([]);
  const [patients, setPatients] = useState([]);
  const [view, setView] = useState('home'); // default view

  useEffect(() => {
    fetchDoctors();
    fetchPatients();
  }, []);

  const fetchDoctors = async () => {
    try {
      const response = await axios.get('/api/doctors');
      setDoctors(response.data);
    } catch (error) {
      console.error('Error fetching doctors:', error);
    }
  };

  const fetchPatients = async () => {
    try {
      const response = await axios.get('/api/patients');
      setPatients(response.data);
    } catch (error) {
      console.error('Error fetching patients:', error);
    }
  };

  const approveDoctor = async (doctorId) => {
    try {
      const response = await axios.post(`/api/doctors/approve/${doctorId}`);
      if (response.data.success) {
        alert('Doctor approved successfully');
        fetchDoctors(); // Refresh the list after approval
      }
    } catch (error) {
      console.error('Error approving doctor:', error);
    }
  };

  const renderContent = () => {
    switch (view) {
      case 'viewDoctors':
        return (
          <Card>
            <Card.Body>
              <h3>Doctor List</h3>
              <Table striped bordered hover>
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Specialization</th>
                    <th>Status</th>
                  </tr>
                </thead>
                <tbody>
                  {doctors.map((doctor) => (
                    <tr key={doctor.id}>
                      <td>{doctor.id}</td>
                      <td>{doctor.name}</td>
                      <td>{doctor.email}</td>
                      <td>{doctor.specialization}</td>
                      <td>{doctor.status}</td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            </Card.Body>
          </Card>
        );
      case 'approveDoctors':
        return (
          <Card>
            <Card.Body>
              <h3>Approve Doctors</h3>
              <Table striped bordered hover>
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Specialization</th>
                    <th>Status</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {doctors.map((doctor) => (
                    <tr key={doctor.id}>
                      <td>{doctor.id}</td>
                      <td>{doctor.name}</td>
                      <td>{doctor.email}</td>
                      <td>{doctor.specialization}</td>
                      <td>{doctor.status}</td>
                      <td>
                        {doctor.status === 'pending' ? (
                          <Button
                            variant="success"
                            size="sm"
                            onClick={() => approveDoctor(doctor.id)}
                          >
                            Approve
                          </Button>
                        ) : (
                          'Approved'
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            </Card.Body>
          </Card>
        );
      case 'viewPatients':
        return (
          <Card>
            <Card.Body>
              <h3>Patient List</h3>
              <Table striped bordered hover>
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Condition</th>
                  </tr>
                </thead>
                <tbody>
                  {patients.map((patient) => (
                    <tr key={patient.id}>
                      <td>{patient.id}</td>
                      <td>{patient.name}</td>
                      <td>{patient.email}</td>
                      <td>{patient.condition}</td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            </Card.Body>
          </Card>
        );
      case 'home':
      default:
        return (
          <div className="text-center mt-5">
            <h1>Care&Cure</h1>
          </div>
        );
    }
  };

  return (
    <Container fluid>
      <Row>
        <Col md={2} className="bg-white p-3 vh-100">
          <Nav className="flex-column justify-content-between h-100">
            <Button variant="primary" size="sm" className="mb-3" onClick={() => setView('viewDoctors')}>
              View Doctor List
            </Button>
            <Button variant="success" size="sm" className="mb-2" onClick={() => setView('approveDoctors')}>
              Approve Doctor
            </Button>
            <Button variant="secondary" size="sm" className="mb-2" onClick={() => setView('viewPatients')}>
              View Patient List
            </Button>
            <Button variant="warning" size="sm" className="mb-2" onClick={() => setView('approvePatients')}>
              Approve Patients
            </Button>
          </Nav>
        </Col>
        <Col md={10}>
          {renderContent()}
        </Col>
      </Row>
    </Container>
  );
};

export default AdminPage;
